// For PNG images
declare module "*.png" {
  const value: string;
  export default value;
}

// For JPG images
declare module "*.jpg" {
  const value: string;
  export default value;
}

// For JPEG images
declare module "*.jpeg" {
  const value: string;
  export default value;
}

// For SVG images
declare module "*.svg" {
  const value: string;
  export default value;
}
